import React, {Component} from "react";

class Story extends Component {
    render(){
        const {title, story, url} = this.props
        function fallback() {
            this.src='img/fallback.jpg';
            console.log("error");
        }
        return(
            <div className="story">
                <img src={url} onError={() => (fallback())}/>
                <h2>{title}</h2>
                <p>{story}</p>
            </div>
        );
    }
}

export default Story;
