import React, { isValidElement } from 'react';
import './App.css';
import Story from './components/Story';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      stories: [],
      listings: [],
      events: [],
      offers: []
    };
    this.handleUpdate = this.handleUpdate.bind(this);
  }

  componentDidMount() {
    const fetchData = async (getData) => {
      let response = await fetch(`https://sv-reqres.now.sh/api/${getData}`);
      let data = await response.json();
      return data;
    }

    fetchData("listings").then( data => {
      console.log(data.data);
      this.setState({
        isLoaded: true,
        stories: data.data,
        // listings: data.data
      })
    })

    fetchData("events").then( data => {
      console.log(data.data);
      this.setState({
        isLoaded: true,
        events: data.data,
      })
    })
    fetchData("offers").then( data => {
      console.log(data.data);
      this.setState({
        isLoaded: true,
        offers: data.data,
      })
    })

  }
  handleUpdate = (update) => {
    this.setState(state => ({
      stories: update
    }));
  }


  render() {
    const { stories } = this.state;

    return(
      <div className="wrapper">
        <nav className="menu">
          <button className="selected" >All</button>
          <button onClick={this.handleUpdate(listings)}>Listings</button>
          <button onClick={this.handleUpdate(events)}>Events</button>
          <button onClick={this.handleUpdate(offers)}>Offers</button>
        </nav>
      <div className="grid">
        {stories.map((story, idx) => (
          <Story key={idx} title={story.title} story={story.description} url={story.mediaurl}/>
        ))}
      </div>
    </div>
    )}
}

export default App;
