import React, {Component} from "react";

class Story extends Component {
    render(){
        const {title, story, url, idx} = this.props
        return(
            <div className="story" >
                <h2><span>{idx}</span>{title}</h2>
                <div className="picture">
                    <img onError={() => this.img.src = '../images/fallback.jpg'} src={url} ref={img => this.img = img}/>
                </div>
            </div>
        );
    }
}

export default Story;
