import React, { isValidElement } from 'react';
import './App.css';
import Story from './components/Story';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      stories: [],
      listings: [],
      events: [],
      offers: [],
      currentItemIdx: 1,
      currentLayout: 1,
      layouts: {
        1 : 5,
        2 : 5,
        3 : 3,
        4 : 2,
      }
    };
    Array.prototype.next = function() {
      return this[++this.current];
    };
    Array.prototype.prev = function() {
        return this[--this.current];
    };
    Array.prototype.current = 0;
    this.handleUpdate = this.handleUpdate.bind(this);
  }

  componentDidMount() {
    // Function to fetch data from API
    const fetchData = async (getData) => {
      let response = await fetch(`https://sv-reqres.now.sh/api/${getData}/?per_page=15`);
      let data = await response.json();
      return data;
    }
    // Different urls to fetch
    const urls = [ "listings"]

    // Map over them and load them to the state
    urls.map((url) => {
      fetchData(url).then( data => {
        console.log(data.data);
        this.setState((state) => ({
          // append all the urls to stories so that all datas shows initially
          stories: [...state.stories, ...data.data],
          // put them in their own variables individually so we can swap between them
          [url]: data.data
        }))
      })
    })
  }

  handleUpdate(update, e){
    this.setState(state => ({
      stories: update
    }));
  }

  handleStories(e){
    e.preventDefaut();
    const { currentItemIdx, currentLayout, layouts } = this.state

    // go to the next layout
    if (currentLayout === Object.keys(layouts).length){
      this.setState({
        currentLayout: 1
      })
    } else {
      this.setState(state => ({
        currentItemIdx: state.currentItemIdx + state.layouts[currentLayout],
        currentLayout: state.currentLayout + 1
      }));
    }
  }



  render() {
    const { stories, listings, events, offers } = this.state;
    const { currentItemIdx, currentLayout, layouts} = this.state;

    let filteredStories = stories.filter((elem, idx) => {
      if( idx >= currentItemIdx && idx < currentItemIdx + layouts[currentLayout] ) {
        return true;
      }
    })
    return(
      <div className="wrapper">
        <div className="grid layout2">
          { filteredStories.map((story, idx) => {
            return <Story key={idx} title={story.title} story={story.description} url={story.mediaurl} idx={idx}/>
          }) }
        </div>
        <nav>
          <div className="controls">
            <a href="" onClick="handleStories">Prev.</a>
            <a href="" onClick={this.handleStories.bind(this, e)}>Next</a>
          </div>
        </nav>
    </div>
    )}
}

export default App;
