import React, { isValidElement } from 'react';
import './App.css';
import Story from './components/Story';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      listing: [],
      events: [],
      offers: []
    };
  }

  componentDidMount() {
    const fetchData = async (getData) => {
      let response = await fetch(`https://sv-reqres.now.sh/api/${getData}`);
      let data = await response.json();
      return data;
    }
    fetchData("listings").then( data => {
       console.log(data.data);
       this.setState({
         isLoaded: true,
         listings: data.data,
       })
     })

    fetchData("events").then( data => {
      console.log(data.data);
      this.setState({
        isLoaded: true,
        events: data.data,
      })
    })
    fetchData("offers").then( data => {
      console.log(data.data);
      this.setState({
        isLoaded: true,
        offers: data.data,
      })
    })
  }

  render() {
    const { stories } = this.state;
    return(
      <div className="wrapper">
        <nav className="menu">
          <button className="selected">All</button>
          <button>Listings</button>
          <button>Events</button>
          <button>Offers</button>
        </nav>
      <div className="grid">
        {stories.map((story, idx) => (
          <Story key={idx} title={story.title} story={story.description} url={story.mediaurl}/>
        ))}
      </div>
    </div>
    )}
}

export default App;
